import React, { useContext, useEffect, useState } from 'react';
import contextData from '../../context/MainContext';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import URL from '../../URL';
import { PromoLoading } from '../Loaders/SkeletonLoader';
import { Link, useLocation } from 'react-router-dom';

import { ProductLoading } from '../Loaders/SkeletonLoader';
import { SliderProducts } from '../ProductsCards/SliderProducts';

const SubCategorySliding = (props) => {

    const data = useContext(contextData);

    const [subcategories, setsubcategories] = useState([]);


    // const { subcategories } = data;
    const [limit, setLimit] = useState(8);
    const [hasMore, setHasMore] = useState(true);

    const fetchMoreData = () => {
        // a fake async api call like which sends...
        // few more records in .5 secs ayse hi bnaye hain...
        setTimeout(() => {
            setLimit(limit + 4);
        }, 700);
    };


    useEffect(() => {
        setsubcategories(data.subcategories);
    }, [data.subcategories]);

    const options = {
        autoplay: true,
        autoplayHoverPause: true,
        loop: true,
        margin: 10,
        nav: false,
        dots: true,
        responsive: {
            0: {
                items: 2
            },
            600: {
                items: 3
            },
            1000: {
                items: 4
            }
        }
    };

    return (
        <>
            <div class="py-3 osahan-promos">
                <div class="d-flex align-items-center mb-3">
                    <h5 class="m-0">Promos for you</h5>
                </div>
                {data.isLoading ? <>
                    <PromoLoading />
                </> :
                    <div class="promo-sliders pb-0 mb-0">
                        {subcategories.length ? (
                            <OwlCarousel className='owl-theme' {...options}>
                                {subcategories.map((item, i) => {
                                    return (
                                        <div class="col-4 col-lg-2 col-md-3 mb-3">
                                            <div class="list-card bg-white h-100 rounded overflow-hidden position-relative shadow-sm">
                                                <div class="p-4">

                                                    <Link state={location.pathname} to={"/" + (item.name + " delivery in gorakhpur").replace(/\s/g, "-").toLowerCase() + "/" + item.id + "/" + item.name}>
                                                        <img src={URL + "/images/category_images/" + item.image} class="img-fluid item-img w-100 mb-3"
                                                            alt={item.name + " in Gorakhpur | SuperG.in is an online vegetable, fruit, cake ,chicken, and grocery delivery website and app in Gorakhpur , Which deliver you home at very low prices. Vegetables & Fruits delivery in Gorakhpur, Grocery delivery in Gorakhpur, Chicken & Fish delivery in Gorakhpur"}
                                                            title={item.name + " delivery in Gorakhpur | Vegetables & Fruits delivery in Gorakhpur, Grocery delivery in Gorakhpur, Chicken & Fish delivery in Gorakhpur"}

                                                        />
                                                        <a class="small-reward-dt hover-btn" >{item.name}</a>
                                                    </Link>
                                                </div>
                                            </div>
                                        </div>
                                    )
                                })}
                            </OwlCarousel>
                        ) : null}
                    </div>
                }
            </div>
        </>
    )

}

export default SubCategorySliding;
