import React, { useContext, useEffect, useState } from 'react';
import contextData from '../../context/MainContext';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import URL from '../../URL';
import { PromoLoading } from '../Loaders/SkeletonLoader';

const Promo = (props) => {

    const data = useContext(contextData);

    const [bannerData, setBannerData] = useState([]);

    useEffect(() => {
        setBannerData(data.banners);
    }, [data.banners]);

    const options = {
        autoplay: true,
        autoplayHoverPause: true,
        loop: true,
        margin: 10,
        nav: false,
        dots: true,
        responsive: {
            0: {
                items: 2
            },
            600: {
                items: 4
            },
            1000: {
                items: 5
            }
        }
    };

    return (
        <>
            <div class="py-3 osahan-promos">
                <div class="d-flex align-items-center mb-3">
                    <h5 class="m-0">Promos for you</h5>
                </div>
                {data.isLoading ? <>
                    <PromoLoading />
                </> :
                    <div class="promo-sliders pb-0 mb-0">
                        {bannerData.length ? (
                            <OwlCarousel className='owl-theme' {...options}>
                                {bannerData.map((item, i) => {
                                    return (
                                        <div class="">
                                            <div class="product-item mb-30">
                                                <a href="single_product_view.html" class="product-img">
                                                    <img src="images/product/img-1.jpg" alt="" />
                                                    <div class="product-absolute-options">
                                                        <span class="offer-badge-1">6% off</span>
                                                        <span class="like-icon" title="wishlist"></span>
                                                    </div>
                                                </a>
                                                <div class="product-text-dt">
                                                    <p>Available<span>(In Stock)</span></p>
                                                    <h4>Product Title Here</h4>
                                                    <div class="product-price">$12 <span>$15</span></div>
                                                    <div class="qty-cart">
                                                        <div class="quantity buttons_added">
                                                            <input type="button" value="-" class="minus minus-btn" />
                                                            <input type="number" step="1" name="quantity" value="1" class="input-text qty text" />
                                                            <input type="button" value="+" class="plus plus-btn" />
                                                        </div>
                                                        <span class="cart-icon"><i class="uil uil-shopping-cart-alt"></i></span>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    )
                                })}
                            </OwlCarousel>
                        ) : null}
                    </div>
                }
            </div>
        </>
    )

}

export default Promo;
