import React, { useContext, useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router';
import { useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom';
import MainData from '../context/MainContext';
import { useDisclosure, useMediaQuery } from '@chakra-ui/react';
import { MdOutlineShoppingCart, MdArrowForwardIos } from 'react-icons/md';
import { BiCategory, BiSearchAlt2 } from 'react-icons/bi';
import { GiPartyPopper } from 'react-icons/gi';
import { AiOutlineLogin } from 'react-icons/ai';
import { BsWhatsapp, BsFillTelephoneFill } from 'react-icons/bs';
import { constants } from '../URL';
import Drawer from './comman/Drawer';

const Header = () => {

  const data = useContext(MainData);

  console.log('all data', data)
  localStorage.setItem("cartItems", JSON.stringify(data.cartItems));
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [isNotSmallerScreen] = useMediaQuery("(min-width:1024px)");
  const { products, logOut, totalItems, subcategories, cartItems, auth } = data;
  const { pathname } = useLocation();
  const [searchedProduct, setSearchedProduct] = useState(products);
  const [searchTerm, setSearchTerm] = useState('');
  const [focused, setFocused] = React.useState(false)
  const onFocus = () => setFocused(true);
  const onBlur = () => setFocused(false);
  const wrapperRef = useRef(null);
  const navigate = useNavigate();

  const useOutsideAlerter = (ref) => {
    useEffect(() => {
      /**
       * Alert if clicked on outside of element
       */
      function handleClickOutside(event) {
        if (ref.current && !ref.current.contains(event.target)) {
          onBlur();
        }
      }

      // Bind the event listener
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        // Unbind the event listener on clean up
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [ref]);
  }

  useOutsideAlerter(wrapperRef);

  useEffect(() => {
    if (products.length != 0) {
      const filteredClubs = products.filter(Club => {
        let ClubLowercase = (
          Club.product_name
        ).toLowerCase();
        let searchTermLowercase = searchTerm.toLowerCase();
        return ClubLowercase.indexOf(searchTermLowercase) > -1;
      });
      setSearchedProduct(filteredClubs);
    }
  }, [searchTerm]);

  useEffect(() => {
    setSearchedProduct(products)
  }, [products]);

  const GetTotal = cartItems.reduce(function (a, b) {
    const price = (b.price) - ((b.price) * (b.discount / 100))
    return a + Number(price * b["itemQuant"]);
  }, 0);

  return (
    <>
      <div id="category_model" class="header-cate-model main-gambo-model modal fade" tabindex="-1" role="dialog" aria-modal="false">
        <div class="modal-dialog category-area" role="document">
          <div class="category-area-inner">
            <div class="modal-header">
              <button type="button" class="close btn-close" data-dismiss="modal" aria-label="Close">
                <i class="uil uil-multiply"></i>
              </button>
            </div>
            <div class="category-model-content modal-content">
              <div class="cate-header">
                <h4>Select Category</h4>
              </div>
              <ul class="category-by-cat">
                <li>
                  <a href="#" class="single-cat-item">
                    <div class="icon">
                      <img src="images/category/icon-1.svg" alt="" />
                    </div>
                    <div class="text"> Fruits and Vegetables </div>
                  </a>
                </li>

              </ul>
              <a href="#" class="morecate-btn"><i class="uil uil-apps"></i>More Categories</a>
            </div>
          </div>
        </div>
      </div>




      <div id="search_model" class="header-cate-model main-gambo-model modal fade" tabindex="-1" role="dialog" aria-modal="false">
        <div class="modal-dialog search-ground-area" role="document">
          <div class="category-area-inner">
            <div class="modal-header">
              <button type="button" class="close btn-close" data-dismiss="modal" aria-label="Close">
                <i class="uil uil-multiply"></i>
              </button>
            </div>
            <div class="category-model-content modal-content">
              <div class="search-header">
                <form action="#">
                  <input type="search" placeholder="Search for products..." />
                  <button type="submit"><i class="uil uil-search"></i></button>
                </form>
              </div>
              <div class="search-by-cat">
                <a href="#" class="single-cat">
                  <div class="icon">
                    <img src="images/category/icon-1.svg" alt="" />
                  </div>
                  <div class="text">
                    Fruits and Vegetables
                            </div>

                </a>
              </div>
            </div>
          </div>
        </div>
      </div>





      <div class="bs-canvas bs-canvas-left position-fixed bg-cart h-100">
        <div class="bs-canvas-header side-cart-header p-3 ">
          <div class="d-inline-block  main-cart-title">My Cart <span>(2 Items)</span></div>
          <button type="button" class="bs-canvas-close close" aria-label="Close"><i class="uil uil-multiply"></i></button>
        </div>
        <div class="bs-canvas-body">
          <div class="cart-top-total">
            <div class="cart-total-dil">
              <h4>Gambo Super Market</h4>
              <span>$34</span>
            </div>
            <div class="cart-total-dil pt-2">
              <h4>Delivery Charges</h4>
              <span>$1</span>
            </div>
          </div>
          <div class="side-cart-items">
            <div class="cart-item">
              <div class="cart-product-img">
                <img src="images/product/img-1.jpg" alt="" />
                <div class="offer-badge">6% OFF</div>
              </div>
              <div class="cart-text">
                <h4>Product Title Here</h4>
                <div class="cart-radio">
                  <ul class="kggrm-now">
                    <li>
                      <input type="radio" id="a1" name="cart1" />
                      <label for="a1">0.50</label>
                    </li>

                  </ul>
                </div>
                <div class="qty-group">
                  <div class="quantity buttons_added">
                    <input type="button" value="-" class="minus minus-btn" />
                    <input type="number" step="1" name="quantity" value="1" class="input-text qty text" />
                    <input type="button" value="+" class="plus plus-btn" />
                  </div>
                  <div class="cart-item-price">$10 <span>$15</span></div>
                </div>

                <button type="button" class="cart-close-btn"><i class="uil uil-multiply"></i></button>
              </div>
            </div>
            <div class="cart-item">
              <div class="cart-product-img">
                <img src="images/product/img-2.jpg" alt="" />
                <div class="offer-badge">6% OFF</div>
              </div>
              <div class="cart-text">
                <h4>Product Title Here</h4>
                <div class="cart-radio">
                  <ul class="kggrm-now">
                    <li>
                      <input type="radio" id="a5" name="cart2" />
                      <label for="a5">0.50</label>
                    </li>

                  </ul>
                </div>
                <div class="qty-group">
                  <div class="quantity buttons_added">
                    <input type="button" value="-" class="minus minus-btn" />
                    <input type="number" step="1" name="quantity" value="1" class="input-text qty text" />
                    <input type="button" value="+" class="plus plus-btn" />
                  </div>
                  <div class="cart-item-price">$24 <span>$30</span></div>
                </div>
                <button type="button" class="cart-close-btn"><i class="uil uil-multiply"></i></button>
              </div>
            </div>
          </div>
        </div>
        <div class="bs-canvas-footer">
          <div class="cart-total-dil saving-total ">
            <h4>Total Saving</h4>
            <span>$11</span>
          </div>
          <div class="main-total-cart">
            <h2>Total</h2>
            <span>$35</span>
          </div>
          <div class="checkout-cart">
            <a href="#" class="promo-code">Have a promocode?</a>
            <a href="#" class="cart-checkout-btn hover-btn">Proceed to Checkout</a>
          </div>
        </div>
      </div>


    </>
  );

}

export default Header;
